@import url("https://fonts.googleapis.com/css2?family=Poppins&family=Source+Sans+Pro:wght@300&display=swap");

$color-primary: #f2e529;
$color-grad-1: #d9cc1e;
$color-grad-2: #bfb417;

$color-blue-1: #6829f2;
$color-blue-2: #3300a6;

$color-grey-light-1: #f9f5f3; // Light background
$color-grey-light-2: #f2efee; // Light lines
$color-grey-light-3: #bfbbba; // Light text (placeholder)
$color-grey-dark-1: #0d0d0d; // Normal text
$color-grey-dark-2: #918581; // Lighter text

$gradient: linear-gradient(to right bottom, $color-grad-1, $color-grad-2);

$tablet-width: 768px;
$desktop-width: 1024px;

$poppins: "Poppins", sans-serif;
$source: "Source Sans Pro", sans-serif;

* {
  margin: 0;
  padding: 0;
}

html {
  box-sizing: border-box;
  width: 100vw;
  height: 100vh;
}

body {
  background-color: #f2e529;
  background-image: linear-gradient(#f2eda2 1px, transparent 1px),
    linear-gradient(to right, #f2eda2 1px, #f2e529 1px);
  background-size: 20px 20px;
  overflow: hidden;
}

li {
  list-style: none;
}

